mapper.select:
SELECT `id`   AS `id`,
       `text` AS `text`

FROM `bbq_bbs`.`notice_boards`
where `id` = #{id}
LIMIT 1;

SELECT `id`   AS `id`,
       `text` AS `text`

FROM `bbq_bbs`.`bp_boards`
where `id` = #{id}
LIMIT 1;

SELECT COUNT(0) FROM `bbq_bbs`.`notice` AS `notice`
WHERE (REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`content`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%'))
AND `board_id` = #{boardId}
# WHERE `board_id` = #{boardId}
AND `board_id` != 'qna' AND `board_id` != 'pi' AND `board_id` != 'pr'
AND `qna_board_id` = 'qna'

SELECT `notice`.`index` AS `index`, `notice`.`id` AS `id`,
`notice`.`board_id` AS `boardId`, `notice`.`title` AS `title`, `notice_board`.`text` AS `text`, `notice`.`content` AS `content`, `notice`.`board_id` AS `boardId`, `notice`.`view` AS `view`, `notice`.`important_flag` AS `isImportant`, `notice`.`written_on` AS `writtenOn`, `notice`.`modified_on` AS `modifiedOn`, `notice`.`new_flag` AS `isNew`, `notice`.`qna_board_id` AS `qnaBoardId`, `notice`.`comment_count` AS `commentCount` FROM `bbq_bbs`.`notice` AS `notice` LEFT JOIN `bbq_bbs`.`notice_boards` AS `notice_board` on `notice_board`.`id` = `notice`.`board_id` WHERE (REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`content`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%'))
AND `board_id` = #{boardId}
AND `board_id` != 'qna' AND `board_id` != 'pi' AND `board_id` != 'pr'
AND `qna_board_id`= #{qid}
GROUP BY `notice`.`index` ORDER BY `notice`.`index` DESC LIMIT ${limit} OFFSET ${offset}

SELECT `bp_article`.`index` AS `index`, `bp_article`.`id` AS `id`, `bp_article`.`bp_board_id` AS `bpBoardId`, `bp_article`.`name` AS `name`, `bp_article`.`email` AS `email`, `bp_article`.`contact` AS `contact`, `bp_article`.`company` AS `company`, `bp_article`.`request_date` AS `requestDate`, `bp_article`.`product_info` AS `productInfo`,
`bp_article`.`request_price` AS `requestPrice`, `bp_article`.`pay` AS `pay`, `bp_article`.`request_evidence` AS `requestEvidence`, `bp_article`.`reply_sms` AS `replySms`, `bp_article`.`reply_email` AS `replyEmaiil`, `bp_article`.`title` AS `title`, `bp_article`.`content` AS `content`, `bp_article`.`written_on` AS `writtenOn`, `bp_article`.`modified_on` AS `modifiedOn`, `bp_article`.`comment_count` AS `commentCount`, `bp_board`.`text` AS `text`
FROM `bbq_bbs`.`bp_articles` AS `bp_article` LEFT JOIN `bbq_bbs`.`bp_boards` AS `bp_board` on `bp_board_id` = `bp_board`.`id` WHERE (REPLACE(`title`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`bp_board`.`id`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`company`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`bp_article`.`id`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`name`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%') OR REPLACE(`content`, ' ', '') LIKE CONCAT('%', REPLACE(#{keyword},' ',''),'%'))
AND `bp_board_id` = #{bpBoardId}
GROUP BY `bp_article`.`index` ORDER BY `bp_article`.`index` DESC LIMIT ${limit} OFFSET ${offset}

SELECT `bp_article`.`index` AS `index`, `bp_article`.`id` AS `id`, `bp_article`.`bp_board_id` AS `bpBoardId`, `bp_article`.`name` AS `name`, `bp_article`.`email` AS `email`, `bp_article`.`contact` AS `contact`, `bp_article`.`company` AS `company`, `bp_article`.`request_date` AS `requestDate`, `bp_article`.`product_info` AS `productInfo`,
`bp_article`.`request_price` AS `requestPrice`, `bp_article`.`pay` AS `pay`, `bp_article`.`request_evidence` AS `requestEvidence`, `bp_article`.`reply_sms` AS `replySms`, `bp_article`.`reply_email` AS `replyEmaiil`, `bp_article`.`title` AS `title`, `bp_article`.`content` AS `content`, `bp_article`.`written_on` AS `writtenOn`, `bp_article`.`modified_on` AS `modifiedOn`, `bp_article`.`comment_count` AS `commentCount`, `bp_board`.`text` AS `text`
FROM `bbq_bbs`.`bp_articles` AS `bp_article` LEFT JOIN `bbq_bbs`.`bp_boards` AS `bp_board` on `bp_board_id` = `bp_board`.`id` WHERE `bp_article`.`id` = #{id} GROUP BY `bp_article`.`index` ORDER BY `bp_article`.`index` DESC LIMIT ${limit} OFFSET ${offset}

SELECT `index` AS `index`, `notice_board`.`text` AS `text`, `notice`.`id` AS `id`, `notice`.`board_id` AS `boardId`, `notice`.`title` AS `title`, `notice`.`important_flag` AS `isImportant`, `notice`.`content` AS `content`, `notice`.`view` AS `view`, `notice`.`written_on` AS `writtenOn`, `notice`.`modified_on` AS `modifiedOn`, `notice`.`new_flag` AS `isNew`, `notice`.`comment_count` AS `commentCount`
FROM `bbq_bbs`.`notice` AS `notice` LEFT JOIN `bbq_bbs`.`notice_boards` AS `notice_board` on `notice_board`. `id` = `notice`.`board_id` WHERE `index` = #{index}
LIMIT 1;

mapper.update:
UPDATE `bbq_bbs`.`notice` SET
`board_id` = #{boardId}, `id` = #{id}, `title` = #{title}, `content` = #{content}, `view` = #{view}, `important_flag` = IFNULL(#{isImportant}, DEFAULT(`important_flag`)), `written_on` = IFNULL(#{writtenOn}, DEFAULT(`written_on`)), `modified_on` = IFNULL(#{modifiedOn}, DEFAULT(`modified_on`)), `comment_count` = #{commentCount}
WHERE `index` = #{index}
