mapper.select:
select `index` as `index`, `title` as `title`
from bbqmall.category
where `detail_index` = #{cid}

select sum(`price` * `quantity`  / sale_quantity) from bbqmall.wishlist
     LEFT JOIN `bbqmall`.`products` AS `product` on `wishlist`.`product_index` = `product`.`product_index`
where id = #{id}

select sum(`quantity`) from bbqmall.wishlist
      LEFT JOIN `bbqmall`.`products` AS `product` on `wishlist`.`product_index` = `product`.`product_index`
where id = #{id}
