mapper.select:
select `index` as `index`, `title` as `title`
from bbqmall.category
where `detail_index` = #{cid}

select sum(`price` * `quantity`  / sale_quantity) from bbqmall.wishlist
     LEFT JOIN `bbqmall`.`products` AS `product` on `wishlist`.`product_index` = `product`.`product_index`
where id = #{id}

select sum(`quantity`) from bbqmall.wishlist
      LEFT JOIN `bbqmall`.`products` AS `product` on `wishlist`.`product_index` = `product`.`product_index`
where id = #{id}

SELECT `product_index` AS `productIndex`, `detail_index` AS `detailIndex`, `menu`.`index`AS `menuIndex`, `product_name` AS `productName`, `price` AS `price`, `content` AS `content`, `view` AS `view`, `created_on` AS `createdOn`, `modified_on` AS `modifiedOn`, `amount` AS `amount`, `on_sale` AS onSale, `sale_quantity` AS `saleQuantity` FROM `bbqmall`.`products` LEFT JOIN `bbqmall`.`detail_menu` on `products`.`detail_index` = `detail_menu`.`index` LEFT JOIN `bbqmall`.`menus` AS `menu` on `products`.`detail_index` = `menu`.`index`
WHERE `menu`.`index` = #{cid}
order by view desc order by createdOn desc order by price desc order by price asc order by view desc order by view asc
