mapper.select:
SELECT `email` AS `email`, `id` AS `id`, `password` AS `password`,
`name` AS `name`, `contact` AS `contact`, `birth` AS `birth`, `address_postal` AS `addressPostal`, `address_primary` AS `addressPrimary`, `address_primary` AS `addressPrimary`, `address_secondary` AS `addressSecondary`, `registered_on` AS `registeredOn`
FROM `bbqmall`.`users` WHERE BINARY `email` = #{email} LIMIT 1;

        SELECT `index`        AS `index`,
               `email`        AS `email`,
               `code`         AS `code`,
               `salt`         AS `salt`,
               `created_on`   AS `createdOn`,
               `expires_on`   AS `expiresOn`,
               `expired_flag` AS `isExpired`

        FROM `bbqmall`.`email_auths`
        WHERE BINARY `email` = #{email}
          AND BINARY `code` = #{code}
          AND BINARY `salt` = #{salt}
        LIMIT 1;
# binary: 대소문자 구분. 하는게 좋겠죠?

mapper.update:
UPDATE `bbqmall`.`email_auths`
SET `expired_flag` = true

WHERE BINARY `email` = #{email}
  AND BINARY `code` = #{code}
  AND BINARY `salt` = #{salt}
LIMIT 1

mapper.select:
SELECT `index` AS `index`, `email` AS `email`, `code` AS `code`, `salt` AS `salt`, `created_on` AS `createdOn`, `expires_on` AS `expiresOn`, `expired_flag` AS `isExpired`
FROM `bbqmall`.`email_auths` WHERE `index` = #{index}
LIMIT 1;
